mapper.select:
SELECT BM_NO, ID, NAME, BLOG_NAME
FROM BLOG_M
WHERE ID = #{loginId}
AND PW = #{loginPw}
AND DEL_YN = 0

SELECT RNUM, B_NO, B_TITLE, B_DT, CNT, CTG_NO FROM( SELECT ROW_NUMBER() OVER(PARTITION BY BM_NO ORDER BY B_NO DESC) AS RNUM, B_NO, B_TITLE, B_DT, CNT, BM_NO, CTG_NO FROM ( SELECT B.B_NO, B.B_TITLE, B.B_DT, COUNT(C.CMT_NO) AS CNT, B.BM_NO, B.CTG_NO FROM( SELECT B_NO, B_TITLE, BM_NO, CTG_NO, CASE WHEN FLOOR(SYSDATE - B_DT) > 7 THEN TO_CHAR(B_DT, 'YYYY-MM-DD') || '' WHEN FLOOR(SYSDATE - B_DT) >= 1 THEN FLOOR(SYSDATE - B_DT) || '일전' WHEN FLOOR((SYSDATE - B_DT) * 24) > 0 THEN FLOOR((SYSDATE - B_DT) * 24) || '시간전' WHEN FLOOR((SYSDATE - B_DT) * 24 * 60) > 0 THEN FLOOR((SYSDATE - B_DT) * 24 * 60) || '분전' ELSE '방금전' END AS B_DT FROM BLOG_B
WHERE B_TITLE LIKE '%' || #{search_txt} || '%' OR REGEXP_REPLACE(B_CON, '<[^>]+>') LIKE '%' || #{search_txt} || '%'
) B LEFT OUTER JOIN BLOG_CMT C ON B.B_NO = C.B_NO GROUP BY B.B_NO, B.B_TITLE, B.B_DT, B.BM_NO, B.CTG_NO)) WHERE RNUM BETWEEN #{startCnt} AND #{endCnt} AND BM_NO = #{bmNo}
AND CTG_NO = #{ctgNo}

SELECT B.B_TITLE, B.B_CON, B.B_DT, B.B_NO, B.BM_NO, C.CTG_NAME FROM( SELECT B_TITLE, B_CON, TO_CHAR(B_DT, 'YYYY-MM-DD HH24:MI') AS B_DT, B_NO, BM_NO, CTG_NO FROM( SELECT RANK() OVER(PARTITION BY BM_NO ORDER BY B_NO DESC) AS RNK, B_TITLE, B_CON, B_DT, B_NO, BM_NO, CTG_NO FROM BLOG_B WHERE 1 = 1
AND CTG_NO = #{ctgNo}
AND (B_TITLE LIKE '%' || #{search_txt} || '%' OR REGEXP_REPLACE(B_CON, '<[^>]+>') LIKE '%' || #{search_txt} || '%')
) B WHERE B.RNK = 1 AND BM_NO = #{bmNo}) B LEFT OUTER JOIN BLOG_CATEGORY C ON B.CTG_NO = C.CTG_NO

SELECT COUNT(*)
FROM BLOG_B
WHERE BM_NO = #{bmNo}

	AND CTG_NO = #{ctgNo}	



		AND (B_TITLE LIKE '%' || #{search_txt} || '%'
		OR REGEXP_REPLACE(B_CON, '<[^>]+>') LIKE '%' || #{search_txt} || '%')

SELECT NAME, BLOG_NAME
FROM BLOG_M
WHERE BM_NO = #{bmNo}

SELECT BLOG_NAME
FROM BLOG_M
WHERE BM_NO = #{bmNo}
